    //IE 에서 Quirks mode 에서 감성 Icon Class 가 적용 안되는 bug fix
    //- 금융은 IE 에서 Quirks mode 이다.
    //- `reaction module` 에서는 감성 icon css class 로 `__` prefix 를 사용하는데 (ex. `__reaction__like`)
    //- Quirks mode 에서는 `__` prefix 가 적용된 css class 는 적용이 안된다.
    function updateReactionOpenBtnEmotionClassForIEQuirksBug(){
        var aWelEmotionIcon = jindo.$$('[class*=__reaction__]');

        jindo.$A(aWelEmotionIcon).forEach(function(elEmotionIcon){
            var welElEmotion = jindo.$Element(elEmotionIcon);

            // fetch emotion class
            var sAllClass = welElEmotion.attr('class');
            var sEmotionClass = (sAllClass.match(/__reaction__[a-z]+/) || {})[0];

            if(!sEmotionClass){
                return;
            }

            // remove previous added class
            // ex.
            // origin: "reaction__sad __reaction__like reaction__like"
            // removed: "__reaction__like"
            welElEmotion.className(sAllClass.replace(/([^__]|^)(reaction__[a-z]+)/g, ''));

            // add new emotion class for Quirks Mode
            // `__reaction_like` to `reaction_like`
            welElEmotion.addClass(sEmotionClass.replace(/__(reaction)/, '$1'));
        });
    }

    function addNumberComma(num) {
    	return num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ',');
    }

