    vCommentLinkHandler: function(result) {
        if (result == null || result == 'undefined') {
            return;
        }

        var url = result.url;
        var objectId = result.objectId;
        var commentNo = result.commentNo;

        if (url != null && url != "") {
            //스타캐스트 및 기사 본문 분기
            if (objectId !=null && (objectId.indexOf("event.star.") > -1)) {
                var sProfile = "real";
                if (location.href.indexOf(location.protocol + "//local") > -1) sProfile = "beta";
                else if (location.href.indexOf(location.protocol + "//alpha") > -1) sProfile = "beta";
                else if (location.href.indexOf(location.protocol + "//dev") > -1) sProfile = "beta";
                else if (location.href.indexOf(location.protocol + "//beta") > -1) sProfile = "beta";
                else if (location.href.indexOf(location.protocol + "//test") > -1) sProfile = "beta";
                var sProfileDomain = ("real" != sProfile) ? "test." : "";
                url =  "http://" + sProfileDomain + "m.star.naver.com/comment/redirect?objectId=" + objectId;
            } else if (url.indexOf("m.entertain.naver.com/read") != -1) {
                url = url.replace("m.entertain.naver.com/read", "entertain.naver.com/read");
            } else if (url.indexOf("m.news.naver.com/read.nhn") != -1) {
                url = url.replace("m.news.naver.com/read.nhn","news.naver.com/main/read.nhn") + "&mode=LSD&mid=sec&sid1=001";
            }

            if (commentNo != null && commentNo != "") {
                var splitedUrl = url.split("?");
                var sDomainUrl = splitedUrl[0];
                var paramUrl = splitedUrl[1];
                if (splitedUrl!=null) {
                    if(splitedUrl.length == 2){
                        if (sDomainUrl.indexOf("news.naver.com/main/read.nhn") != -1) {
                            url = sDomainUrl +"?commentNo="+commentNo+"&m_view=1&"+paramUrl;
                        } else if (sDomainUrl.indexOf("entertain.naver.com/read") != -1) {
                            sDomainUrl = sDomainUrl.replace("entertain.naver.com/read", "entertain.naver.com/comment/list");
                            url = sDomainUrl +"?commentNo="+commentNo+"&"+paramUrl;
                        } else if (sDomainUrl.indexOf("m.news.naver.com/poll/vote.nhn") != -1 && paramUrl.indexOf("pollId=188") != -1) {
                            sDomainUrl = sDomainUrl.replace("m.news.naver.com/poll/vote.nhn", "m.news.naver.com/election/nation2016/event/votePromise/index.nhn");
                            url = sDomainUrl + "?commentNo=" + commentNo;
                        } else {
                            url = sDomainUrl +"?commentNo="+commentNo+"&"+paramUrl;
                        }
                    } else {
                        url = sDomainUrl +"?commentNo="+commentNo;
                    }
                }
            } else {
                if (url.indexOf("m.news.naver.com/poll/vote.nhn?pollId=188") != -1) {
                    url = url.replace("m.news.naver.com/poll/vote.nhn?pollId=188", "m.news.naver.com/election/nation2016/event/votePromise/index.nhn");
                }
            }
        }

        if (url != null && url != "") {
            window.location.href = url;
        }
    }
});
